import React, { useState, useEffect } from "react";
import questions from "./cbt_questions_2900_prefixed.json";
import "./App.css";

const TOTAL_QUESTIONS = 25;
const PASS_SCORE = 70;

function shuffleArray(array) {
  return [...array].sort(() => Math.random() - 0.5);
}

function App() {
  const [shuffledQuestions, setShuffledQuestions] = useState([]);
  const [currentQuestionIndex, setCurrentQuestionIndex] = useState(0);
  const [selectedAnswers, setSelectedAnswers] = useState({});
  const [showResults, setShowResults] = useState(false);
  const [score, setScore] = useState(0);
  const [reviewMode, setReviewMode] = useState(false);
  const [timeLeft, setTimeLeft] = useState(45 * 60); // 45 minutes
  const [showExplanation, setShowExplanation] = useState(false);

  useEffect(() => {
    const randomQuestions = shuffleArray(questions).slice(0, TOTAL_QUESTIONS);
    setShuffledQuestions(randomQuestions);
  }, []);

  useEffect(() => {
    if (!showResults && timeLeft > 0) {
      const timer = setInterval(() => setTimeLeft((prev) => prev - 1), 1000);
      return () => clearInterval(timer);
    } else if (timeLeft === 0) {
      handleSubmit();
    }
  }, [timeLeft, showResults]);

  const handleAnswer = (option) => {
    if (showResults || selectedAnswers[currentQuestionIndex] !== undefined) return;
    const correct = shuffledQuestions[currentQuestionIndex].answer.trim();
    const selected = option.trim();

    setSelectedAnswers((prev) => ({
      ...prev,
      [currentQuestionIndex]: selected,
    }));

    if (selected === correct) {
      setScore((prev) => prev + 1);
    }
  };

  const handleSubmit = () => {
    setShowResults(true);
  };

  const handleRestart = () => {
    const newQuestions = shuffleArray(questions).slice(0, TOTAL_QUESTIONS);
    setShuffledQuestions(newQuestions);
    setCurrentQuestionIndex(0);
    setSelectedAnswers({});
    setShowResults(false);
    setScore(0);
    setReviewMode(false);
    setTimeLeft(45 * 60);
    setShowExplanation(false);
  };

  const currentQuestion = shuffledQuestions[currentQuestionIndex];
  const totalScore = Math.min(score, TOTAL_QUESTIONS);
  const percentage = Math.round((totalScore / TOTAL_QUESTIONS) * 100);
  const passed = percentage >= PASS_SCORE;

  const getOptionStyle = (option) => {
    const selected = selectedAnswers[currentQuestionIndex];
    const correct = currentQuestion.answer.trim();
    const isSelected = selected === option;
    const isCorrect = correct === option;

    if (!showResults) {
      return isSelected ? "selected" : "";
    }

    if (reviewMode) {
      if (isCorrect) return "correct";
      if (isSelected && !isCorrect) return "incorrect";
    }

    return isSelected ? (isCorrect ? "correct" : "incorrect") : "";
  };

  return (
    <div className="App">
      {!showResults && (
        <>
          <div className="timer">⏰ 남은 시간: {`${Math.floor(timeLeft / 60)
            .toString()
            .padStart(2, "0")}:${(timeLeft % 60).toString().padStart(2, "0")}`}</div>
          <h2>문제 {currentQuestionIndex + 1} / {TOTAL_QUESTIONS}</h2>
          <p>{currentQuestion.question}</p>
          <div className="options">
            {currentQuestion.options.map((opt, idx) => (
              <div
                key={idx}
                className={`option ${getOptionStyle(opt)}`}
                onClick={() => handleAnswer(opt)}
              >
                {opt}
              </div>
            ))}
          </div>
          <div className="nav-buttons">
            <button onClick={() => setCurrentQuestionIndex((prev) => Math.max(prev - 1, 0))}>
              이전 문제
            </button>
            <button onClick={() => setCurrentQuestionIndex((prev) => Math.min(prev + 1, TOTAL_QUESTIONS - 1))}>
              다음 문제
            </button>
          </div>
          <button onClick={() => setShowExplanation((prev) => !prev)}>해설 보기</button>
          {showExplanation && <div className="explanation">해설: {currentQuestion.explanation}</div>}
          <br />
          <button onClick={handleSubmit}>시험 제출</button>
        </>
      )}

      {showResults && (
        <>
          <h2>시험 종료!</h2>
          <p>점수: {totalScore} / {TOTAL_QUESTIONS}</p>
          <h3>{passed ? "✅ 합격입니다!" : "❌ 불합격입니다."}</h3>
          <button onClick={handleRestart}>다음 시험</button>

          <h3>복습 모드</h3>
          <button onClick={() => setReviewMode(true)}>복습 시작</button>
          {reviewMode &&
            shuffledQuestions.map((q, idx) => {
              const correct = q.answer.trim();
              const selected = selectedAnswers[idx];
              return (
                <div key={idx} className="review-question">
                  <p>{idx + 1}. {q.question}</p>
                  {q.options.map((opt, i) => {
                    let className = "";
                    if (opt === correct) className = "correct";
                    else if (opt === selected && opt !== correct) className = "incorrect";
                    return (
                      <div key={i} className={`option ${className}`}>
                        {opt}
                      </div>
                    );
                  })}
                  <div className="explanation">정답: {correct} <br />해설: {q.explanation}</div>
                </div>
              );
            })}
        </>
      )}
    </div>
  );
}

export default App;
