import React, { useState, useEffect } from "react";
import "./index.css";

function shuffle(array) {
  let currentIndex = array.length,
    randomIndex;

  while (currentIndex !== 0) {
    randomIndex = Math.floor(Math.random() * currentIndex);
    currentIndex--;

    [array[currentIndex], array[randomIndex]] = [
      array[randomIndex],
      array[currentIndex],
    ];
  }
  return array;
}

function App() {
  const [remainingTime, setRemainingTime] = useState(2700); // 45 minutes
  const [authenticated, setAuthenticated] = useState(() => localStorage.getItem("authenticated") === "true");
  const [shuffledQuestions, setShuffledQuestions] = useState([]);
  const [currentIndex, setCurrentIndex] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [showExplanation, setShowExplanation] = useState(false);
  const [score, setScore] = useState(0);
  const [isFinished, setIsFinished] = useState(false);

  useEffect(() => {
    fetch(process.env.PUBLIC_URL + "/cbt_questions_2900_prefixed.json")
      .then(res => res.json())
      .then(data => {
        setShuffledQuestions(shuffle(data).slice(0, 25));
        setRemainingTime(2700); // reset timer when new questions load
      });
  }, [isFinished]);

  useEffect(() => {
    if (!authenticated || isFinished) return;
    const interval = setInterval(() => {
      setRemainingTime(prev => {
        if (prev <= 1) {
          clearInterval(interval);
          setIsFinished(true);
          return 0;
        }
        return prev - 1;
      });
    }, 1000);
    return () => clearInterval(interval);
  }, [authenticated, isFinished]);

  const handlePasswordSubmit = (e) => {
    e.preventDefault();
    const input = e.target.elements.password.value;
    if (input === "0430") {
      setAuthenticated(true);
      localStorage.setItem("authenticated", "true");
    } else {
      alert("비밀번호가 틀렸습니다.");
    }
  };

  const handleOptionClick = (option) => {
    if (selectedOption !== null) return;
    setSelectedOption(option);
    if (option === shuffledQuestions[currentIndex].answer) {
      setScore(score + 1);
    }
    shuffledQuestions[currentIndex].selected = option;
  };

  const handleNext = () => {
    setShowExplanation(false);
    setSelectedOption(null);
    if (currentIndex + 1 < shuffledQuestions.length) {
      setCurrentIndex(currentIndex + 1);
    } else {
      setIsFinished(true);
    }
  };

  const handlePrev = () => {
    if (currentIndex > 0) {
      setSelectedOption(null);
      setShowExplanation(false);
      setCurrentIndex(currentIndex - 1);
    }
  };

  const handleRetry = () => {
    setCurrentIndex(0);
    setScore(0);
    setIsFinished(false);
    setSelectedOption(null);
    setShowExplanation(false);
  };

  if (!authenticated) {
    return (
      <div className="app">
        <h2>시험 입장</h2>
        <form onSubmit={handlePasswordSubmit}>
          <input type="password" name="password" placeholder="비밀번호 입력" />
          <button type="submit">입장</button>
        </form>
      </div>
    );
  }

  if (isFinished) {
    return (
      <div className="app">
        <h2>시험 종료!</h2>
        <p>점수: {score} / {shuffledQuestions.length}</p>
        <p>{score >= 18 ? "✅ 합격입니다!" : "❌ 불합격입니다."}</p>
        <button onClick={handleRetry}>다음 시험</button>
        <h3>복습 모드</h3>
        {shuffledQuestions.map((q, i) => (
          <div key={i} className="review">
            <p><strong>{i + 1}. {q.question}</strong></p>
            {q.options.map((opt, idx) => {
              const isCorrect = opt === q.answer;
              const isSelected = opt === q.selected;
              return (
                <p
                  key={idx}
                  style={{
                    color: isCorrect ? "green" : isSelected ? "red" : "black",
                    fontWeight: isCorrect || isSelected ? "bold" : "normal",
                    backgroundColor: isSelected ? "#fce4ec" : "transparent"
                  }}
                >
                  {opt}
                </p>
              );
            })}
            <p><strong>정답:</strong> {q.answer}</p>
            <p><strong>해설:</strong> {q.explanation}</p>
            <hr />
          </div>
        ))}
      </div>
    );
  }

  const currentQuestion = shuffledQuestions[currentIndex];

  if (shuffledQuestions.length === 0) {
    return <div className="app">문제를 불러오는 중입니다...</div>;
  }

  return (
    <div className="app">
      <h3>⏰ 남은 시간: {Math.floor(remainingTime / 60).toString().padStart(2, "0")}:{(remainingTime % 60).toString().padStart(2, "0")}</h3>
      <h2>문제 {currentIndex + 1} / {shuffledQuestions.length}</h2>
      <p><strong>{currentQuestion.question}</strong></p>
      <ul>
        {currentQuestion.options.map((option, index) => (
          <li key={index}
              className={selectedOption === option
                ? (option === currentQuestion.answer ? "correct" : "wrong")
                : ""}
              onClick={() => handleOptionClick(option)}
              style={{
                backgroundColor: selectedOption === option ? "#fce4ec" : "",
                fontWeight: option === currentQuestion.answer ? "bold" : "normal",
                color: option === currentQuestion.answer
                  ? "green"
                  : selectedOption === option
                    ? "red"
                    : "black"
              }}>
            {option}
          </li>
        ))}
      </ul>
      {selectedOption && (
        <>
          <button onClick={() => setShowExplanation(!showExplanation)}>
            해설 보기
          </button>
          {showExplanation && (
            <div className="explanation">
              <p><strong>정답:</strong> {currentQuestion.answer}</p>
              <p><strong>해설:</strong> {currentQuestion.explanation}</p>
            </div>
          )}
          <div style={{ marginTop: "10px" }}>
            <button onClick={handlePrev} disabled={currentIndex === 0}>이전 문제</button>
            <button onClick={handleNext}>다음 문제</button>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
